import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      expense:0,
      total:0,
      table:[]
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    defaultProps = { expense: 0, total:0, table:[] }
  }
  handleChange(event){
    this.setState({
        expense: event.target.value
      })
  }
  handleSubmit(event){
    event.preventDefault();
    if(this.state.expense){
      this.setState({
        table: [
          ...this.state.table,
          {
            expense: this.state.expense,
          }
        ],
        expense:0,
        total: this.state.total+this.state.expense
      })
    }
  }
  render(){
    return(
      <div>
        <form onSubmit={this.handleSubmit}>
        <input placeholder={"Enter expense"} input={this.state.input} onChange={this.handleChange}></input>
        <button type='submit'>Submit!</button>
        </form>   

        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Expense</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.table.map((item,index)=>{
                return(
                  <tr key={index}>
                  <td>No.{index +1}</td>
                  <td>{item.expense}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        <h1>Total: {this.state.total}</h1>
      </div>
    )
  }
}

export default App;
