import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      input:'',
      submit:'',
      table:[]
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    this.setState({
        input: event.target.value
      })
  }
  handleSubmit(event){
    event.preventDefault();
    if(this.state.input){
      this.setState({
        input: '',
        submit: this.state.input,
        table: [
          ...this.state.table,
          {
            expense: this.state.submit
          }
        ]
      })
    }
  }
  render(){
    return(
      <div>
        <form onSubmit={this.handleSubmit}>
        <input placeholder={"Enter expense"} input={this.state.input} onChange={this.handleChange}></input>
        <button type='submit'>Submit!</button>
        </form>      
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Expense</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.table.sort((a,b)=>b.expense-a.expense).map((item))
            }
          </tbody>
        </table>
      </div>
    )
  }
}

export default App;
